    render()
    {   
        return(
            
            <div>
                <h1>Details for city is {this.state.city} and mealtype {this.props.match.params.id}</h1>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Locality</th>
                            <th>Cuisine</th>
                            <th>Cost</th>
                        </tr>
                    </thead>
                </table>
                {this.handle(this.state.restaurantlist)}
            </div>
        )
    }
    componentDidMount(){
        const meal = parseInt(this.props.match.params.id)
      fetch(`${rest}${this.state.city}/${meal}`,{method:'GET'}).then(res => res.json()).then((data) => {
          this.setState({restaurantlist:data})
      }

      )
    }
        
}
export default Details
