import React ,{Component}from 'react'
import axios from 'axios'

const rest = "http://localhost:8900/restaurantlist/"
class Details extends Component{
    constructor(props){
        super(props)
        this.state={
            city:parseInt(sessionStorage.getItem('city'))?parseInt(sessionStorage.getItem('city')):1,
            restaurantlist:[]
        }
        
    }
    
    render()
    {   const demo = this.state.restaurantlist
        return(
            
            <div>
                <h1>Details for city is {this.state.city} and mealtype {this.props.match.params.id}</h1>
                
            </div>
        )
    }
    componentDidMount(){
        const meal = parseInt(this.props.match.params.id)
        axios.get(`${rest}${this.state.city}/${meal}`).then((response) => {
            this.setState({restaurantlist:response.data});
        })
    }
        
}
export default Details
