import React,{Component} from 'react'

var url = "http://localhost:8900/location";
class Search extends Component{

  constructor() {
    super()
    this.state={
      location:'',
      city:''
    }

}
handleLocation = (data) => {
  if(data){
    return(
      data.map((item) => {
        return(
          <option value={item.city}>{item.city_name}</option>
        )
      })
)
  }


}
handleCity =(event) => {
  return(
    this.state
  )
}
  render(){
    return(
      <div className="container">
        <p id="find-rest">Find the Best restaurants, cafes and bars</p>
          <div id="inputboxes">
            <select id="inputbox1" className="inputbox"  placeholder="Please type a location" onChange={this.handleCity}>
              {this.handleLocation(this.state.location)}
            </select>

            <select  id ="inputbox2"className="inputbox"  placeholder="Search for restaurants"></select>



          </div>

      </div>
    )
  }

  componentDidMount(){
    fetch(url,{method:'GET'}).then((res) => res.json()).then((data) => {return(
      this.setState({location:data})
    )})
  }
}
export default Search
