import React,{Component} from 'react';
import axios from 'axios';
import DetailDisplay from './DetailsDisplay';
import SortFilter from './SortFilter';

const restdetail = "http://localhost:8900/restaurantlist";
class Details extends Component {
   constructor(props){
    super(props)
    this.state={
        city:parseInt(sessionStorage.getItem('city'))?parseInt(sessionStorage.getItem('city')):1,
        restaurantlist:[],
    }
   }
   render(){

   }
   componentDidMount(){
    const meal = parseInt(this.props.match.params.id)
    const city = (this.state.city)?this.state.city:0
    axios.get(`${restdetail}/${city}/${meal}`).then((response)=> {this.setState({restaurantlist:response.data})})
   }
}


export default Details;
