import React, { Component } from 'react'
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import './tabs.css'
import Axios from 'axios';
const url = "http://localhost:8900/restaurantdetail/"
class Details_Tab  extends Component{
  constructor(props){
    super(props)
    this.state ={
      restdetails:''
    }
  }
  handlerest = (data) => {
   if(data){
     data.map((item) => {
       return(
         <div>
           <TabPanel>
          <div>
            <br/>
  <h4>About this place</h4>
  <br />
  <h5>Cuisine</h5>
  <br />
  <h6 style={{fontWeight: 'lighter', fontSize: 15}}>Bakery, Fast Food</h6>
  <br />
  <h5>Average Cost</h5>
  <br />
  <h6 style={{fontWeight: 'lighter', fontSize: 15}}>
    ₹700 for two people (approx.)
  </h6>
</div>

    </TabPanel>
    <TabPanel>
   <div>
  <h5 style={{fontSize: 18}}>Phone number</h5>
  <p style={{color: '#f67082'}}>+91 114004566 </p>
  <br />
  <p style={{fontSize: 18, fontWeight: 600}}>The Big Chilli Bakery</p>
  <p style={{fontSize: 16, color: '#636f88'}}>Shop 1, Plot D, Samruddhi Complex, Chincholi,Mumbai-400002, Maharashtra
  </p>
</div>

    </TabPanel>
         </div>
       )
     })
   }
  }
  render(){
return(
  <div className="container cust-tab px-sm-0">
       <p style={{fontSize:30}}>The Big Chilli Cakery</p>
       <Tabs>
    <TabList>
      <Tab >Overview</Tab>
      &nbsp;&nbsp;&nbsp;
      <Tab >Contact</Tab>
    </TabList>
     {this.handlerest(this.state.restdetails)}
    
   </Tabs>
      </div>
)
  }
  componentDidMount(){
    Axios.get(`${url}${parseInt(this.props.data)}`).then((response) => {
      this.setState({ restdetails:response.data})
    })
    console.log("---->",self.state.restdetails)

  }

} 



 
export default Details_Tab
