import React ,{Component}from 'react'
import {Link} from 'react-router-dom'
import './Details.css'

const rest = "http://localhost:8900/restaurantlist/"
const cuisineurl = "http://localhost:8900/cuisine"
class Details extends Component{
    constructor(props){
        super(props)
        this.state={
            city:parseInt(sessionStorage.getItem('city'))?parseInt(sessionStorage.getItem('city')):1,
            restaurantlist:[],
            cuisine:[]
        }
        
    }
    handlerestaurant =(data) => {
        if(data){
            return(
                data.map((item) => {
                  return(
                   
                    <tr>
                        <Link to={`/restdetails/${item._id}`}>
                    <td>{item.name}</td></Link>
                    <td>{item.locality}</td>
                  <td>{item.Cuisine.map((data,i)=>{
                      return data['name']})}</td>
                    <td>{item.cost}</td>
                    
                </tr>
                   
                  )
                })
                
            )
      
        }
    }
    handlecuisine = (data) => {
        if(data){
            return data.map((item) => {
                return(
                    <div className="mt-1">
                        <input type="radio" value={item._id} name="cuisine" /> {item.name}
                        <br/>
                    </div>
                    
                )
            })
        }

    }
    
    render()
    {   
        return(
            <div className="container-fluid mt-3">
                <div className="row">
                    <div className="col-12 col-sm-2">
                       <div className="row">
                           <div className="col-6 col-sm-12">
                           <p className="side-heading" >Cuisine</p>
                       <input type="radio" value="" name="cuisine" /> All
                       {this.handlecuisine(this.state.cuisine)}
                           </div>
                           <br/>
                           <div className="col-6 col-sm-12">
                           <p className="side-heading">Cost</p>
                       <input className="" type="radio" value="0,400" name="cost" /> 0-500
                       <br/>
                       <input  className="mt-3" type="radio" value="400,700" name="cost" /> 400-700
                       <br/>
                       <input  className="mt-3" type="radio" value="700,1000" name="cost" /> 700-1000
                           </div>
                           <br/>
                           <div className="col-6 col-sm-12">
                           <p className="side-heading">Sort</p>
                       <input  className="" type="radio" value="1" name="Sort" /> Lower to Higher
                       <br/>
                       <input  className="mt-3" type="radio" value="-1" name="Sort" /> Higher to Lower
                           </div>
                       </div>
                       
                       
                       
                
                       
                    </div>
                    <div>
                <table className="table col-12 col-sm-10">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Locality</th>
                            <th>Cuisine</th>
                            <th>Cost</th>
                        </tr>
                    </thead>
                    <tbody>
                    {this.handlerestaurant(this.state.restaurantlist)}
                    </tbody>
                </table>
                
            </div>
                </div>
            </div>

           
        )
    }
    componentDidMount(){
        const meal = parseInt(this.props.match.params.id)
      fetch(`${rest}${this.state.city}/${meal}`,{method:'GET'}).then(res => res.json()).then((data) => {
          this.setState({restaurantlist:data})
      }

      )
      fetch(cuisineurl,{method:'GET'}).then(res => res.json()).then((data) => {
          this.setState({cuisine:data})
      })
    }
    
        
}
export default Details
