import React ,{Component}from 'react'
import {Link} from 'react-router-dom'

const rest = "http://localhost:8900/restaurantlist/"
class Details extends Component{
    constructor(props){
        super(props)
        this.state={
            city:parseInt(sessionStorage.getItem('city'))?parseInt(sessionStorage.getItem('city')):1,
            restaurantlist:[]
        }
        
    }
    handle =(data) => {
        if(data){
            return(
                data.map((item) => {
                  return(
                   
                    <tr>
                        <Link to={`/restdetails/${item._id}`}>
                    <td>{item.name}</td></Link>
                    <td>{item.locality}</td>
                  <td>{item.Cuisine.map((data,i)=>{
                      return data['name']+","+" "})}</td>
                    <td>{item.cost}</td>
                    
                </tr>
                   
                  )
                })
                
            )
      
        }
    }
    
    render()
    {   
        return(
            
            <div>
                <h1>Details for city is {this.state.city} and mealtype {this.props.match.params.id}</h1>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Locality</th>
                            <th>Cuisine</th>
                            <th>Cost</th>
                        </tr>
                    </thead>
                    <tbody>
                    {this.handle(this.state.restaurantlist)}
                    </tbody>
                </table>
                
            </div>
        )
    }
    componentDidMount(){
        const meal = parseInt(this.props.match.params.id)
      fetch(`${rest}${this.state.city}/${meal}`,{method:'GET'}).then(res => res.json()).then((data) => {
          this.setState({restaurantlist:data})
      }

      )
    }
        
}
export default Details
