import React,{Component} from 'react'
import { render } from 'react-dom';

var url = "http://localhost:8900/location";
var resurl ="http://localhost:8900/restauranthome?city=";
class Search extends Component{

  constructor() {
    super()
    this.state={
      location:'',
      city:'',
      restaurant:''
    }

}
handleLocation = (data) => {
  var dup =[]
 if(data){

  for(var i =0;i<data.length;i++){
    for(var j=0;i<dup.length;j++){
      if(dup[j]['city'] == data[i]['city'])
      {
        flag = 1
      }
    }
    dup.push(data[i])


  }
  console.log(dup)


 }
  }
handleCity =(event) => {
    this.setState({city:event.target.value})
    var city = parseFloat(event.target.value);
    fetch(`${resurl}${city}`,{method:'GET'}).then((res) => res.json()).then((data) => { this.setState({restaurant:data})})

}
handleRestaurant = (data) => {
  if(data){
    return(
      data.map((item) => {
        return(
          <option value={item._id}>
          {item.name}
          </option>
        )
      })
    )
  }
}
render(){
  return(
    <div className="container">
    <p id="find-rest">Find the Best restaurants, cafes and bars</p>
      <div id="inputboxes">
        <select id="inputbox1" className="inputbox"  placeholder="Please type a location" onChange={this.handleCity}>
          {this.handleLocation(this.state.location)}
        </select>
        <select  id ="inputbox2"className="inputbox"  placeholder="Search for restaurants">
        {this.handleRestaurant(this.state.restaurant)}
        </select>
      </div>

  </div>
  )

}
  componentDidMount(){
    fetch(url,{method:'GET'}).then((res) => res.json()).then((data) => {return(
      this.setState({location:data})
    )})
  }
}
export default Search
