import React,{Component} from 'react'
var mealtypeurl = "http://localhost:8900/mealtype";
class QuickSearchDisplay extends Component{
    constructor(){
        super()
        this.state={
            mealtype:""
        }
    }
    handleQuickCards = (data) => {
        if(data){
            return(
                data.map((item) => {
                    return(
                        <div className="card">
                            <div className="row no-gutters mt-3">
                            <div class="col-sm-6">
                                <img class="card-img" src="./homeimage.png" alt="Suresh Dasari Card" height="175px"/>

                            </div>
                            <div className="col-sm-6">
                            <div class="card-body">
                        <h5 class="card-title">{item.name}</h5>

                            </div>

                        </div>

                        </div>

                        </div>
                        
                    )
                })
            )
        }

    }
    render(){
        return(
            <div className="row justify-content-around">
                {this.handleQuickCards(this.state.mealtype)} 
            </div>
        )
    }
    componentDidMount(){
        fetch(mealtypeurl,{method:'GET'}).then((res) => res.json()).then((data) => {
            this.setState({mealtype:data})
        })
    }

}

export default QuickSearchDisplay
