import React,{Component} from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';
import DetailDisplay from './DetailsDisplay';
import SortFilter from './SortFilter';

const restdetail = "http://localhost:8900/restaurantlist";
class Details extends Component {
   constructor(props){
    super(props)
    this.state={
        city:parseInt(sessionStorage.getItem('city'))?parseInt(sessionStorage.getItem('city')):1,
        restaurantlist:[],
    }
   }
   handlerestaurant=(data)=>{
    if(data.length === 0){
        return(
            <p className="pl-3">No Restaurants Available</p>
        )
    }
    else if(data){
        return(
            data.map((item) => {
                
              return(
               
                   
                        <div className="cust-card col-sm-8 col-10 mt-3 mt-sm-3 ml-sm-4 ml-4">
                             <Link style={{textDecoration:'none'}}to={`/restdetails/${item._id}`}>
                           <div className="row">
                               <div className="col-6" style={{paddingLeft:0}}>
                                   <img  className="img-responsive w-100 h-100" src={item.thumb} alt=""></img>

                               </div>
                               <div className="col-6 pt-3 cust-card-component" style={{textAlign:'left'}}>
                                    <p id="name"> <Emoji symbol="ğŸ¨" label="restaurant" /> {item.name}</p>
                                    <p>{item.locality}</p>
                                    <p> {item.Cuisine.map((item2,key) =>{ 
                                        if(key === itemlength -1){

                                            return item2['name']
                                        }
                                        else{
                                           return item2['name']+','
                                        }

                                        })}</p>
                                    <p> Rs.{item.cost}</p>

                               </div>

                           </div>
                           </Link>
                        </div>
                
               
            
               
              )
            })
            
        )
  
    }
   }
   render(){
       return(
           <div>
               <SortFilter restdata={this.state.restaurantlist}></SortFilter>
               <div className="col-12 col-sm-10 pt-sm-5 pl-sm-5" style={{textAlign:'left'}}>
                        <p className="availrest">Available restaurants</p>
                       <div className="row">
                       {this.handlerestaurant(this.state.restaurantlist)}
                       </div>
                        
                     
                   
                    
                   </div>

           </div>
       )

   }
   componentDidMount(){
    const meal = parseInt(this.props.match.params.id)
    const city = (this.state.city)?this.state.city:0
    axios.get(`${restdetail}/${city}/${meal}`).then((response)=> {this.setState({restaurantlist:response.data})})
   }
}


export default Details;
