import React ,{Component}from 'react'
import {Link} from 'react-router-dom'
import './Details.css'

const rest = "http://localhost:8900/restaurantlist/"
const cuisineurl = "http://localhost:8900/cuisine"
class Details extends Component{
    constructor(props){
        super(props)
        this.state={
            city:parseInt(sessionStorage.getItem('city'))?parseInt(sessionStorage.getItem('city')):1,
            restaurantlist:[],
            cuisine:[]
        }
        
    }
    handlerestaurant =(data) => {
        if(data){
            return(
                data.map((item) => {
                  return(
                   
                    <tr>
                        <Link to={`/restdetails/${item._id}`}>
                    <td>{item.name}</td></Link>
                    <td>{item.locality}</td>
                  <td>{item.Cuisine.map((data,i)=>{
                      return data['name']})}</td>
                    <td>{item.cost}</td>
                    
                </tr>
                   
                  )
                })
                
            )
      
        }
    }
    handlecuisine = (data) => {
        if(data){
            return data.map((item) => {
                return(
                    <div className="mt-1">
                        <input type="radio" value={item._id} name="cuisine" /> {item.name}
                        <br/>
                    </div>
                    
                )
            })
        }

    }
    
    render()
    {   
        return(
           <div className="container-fluid">
               <div className="row">
                   <div className="col-12 col-sm-2">

                   </div>

               </div>

           </div>
        )
    }
    componentDidMount(){
        const meal = parseInt(this.props.match.params.id)
      fetch(`${rest}${this.state.city}/${meal}`,{method:'GET'}).then(res => res.json()).then((data) => {
          this.setState({restaurantlist:data})
      }

      )
      fetch(cuisineurl,{method:'GET'}).then(res => res.json()).then((data) => {
          this.setState({cuisine:data})
      })
    }
    
        
}
export default Details
