import React,{Component} from 'react'
var mealtypeurl = "http://192.168.43.245:8900/mealtype";
class QuickSearchDisplay extends Component{
    constructor(){
        super()
        this.state={
            mealtype:""
        }
    }
    handleQuickCards = (data) => {
        if(data){
            return(
                data.map((item) => {
                    var name = item.name
                     name = name.charAt(0).toUpperCase() + name.slice(1)
                    return(
                        <div className="card cardmain   mt-4 ml-6 ml-sm-0 mr-sm-4" >
                        <img className="card-img-left" src="./shutterstock_1154073754.png" alt="Card image cap" height="150px"/>
                        <div class="card-body">
                        <h5 class="card-title card_name">{name}</h5>
                        <p class="card-text goodfood" >Have a Good food</p>
                    </div>
                    </div>
                        
                    )
                })
            )
        }

    }
    render(){
        return(
            <div className="row mb-5">
                {this.handleQuickCards(this.state.mealtype)} 
            </div>
        )
    }
    componentDidMount(){
        fetch(mealtypeurl,{method:'GET'}).then((res) => res.json()).then((data) => {
            this.setState({mealtype:data})
        })
    }

}

export default QuickSearchDisplay
